function [sk,f] = BCSparse(bccode,bcvalue,sk,f)%applies boundary conditions for sparce system using blastingconst=1.0e+8;[m n]   = size(bccode);numbc = m;for i=1:1:numbc                                                              NodeNo=bccode(i,1)+1;          n1=bccode(i,2);      n2=bccode(i,3);      n3=bccode(i,4);            u1=bcvalue(i,2);      u2=bcvalue(i,3);      u3=bcvalue(i,4);                        i3=3*NodeNo;      i2=i3-1;      i1=i3-2;	        if(n1==1)                                              	  	  sk(i1,i1)  =sk(i1,i1) *const;                             f(i1)      =u1*sk(i1,i1);                                                                                              end	        if(n2==1)                                              	  	  sk(i2,i2)  =sk(i2,i2) *const;                             f(i2)      =u2*sk(i2,i2);                                                                                              end	        if(n3==1)                                              	  	  sk(i3,i3)  =sk(i3,i3) *const;                             f(i3)      =u3*sk(i3,i3);                                                                                             end	  end                                                               a=1;