function me = ElementLHS4ConPen(nnpe,nqptv,wtq,bigN,bigNprime,detj,dampfac)% forms elemental LHS  matrix for consistent penalty iend=nnpe;me = zeros(iend,iend);                                       for   k=1:1:nqptv       bigNqp = bigN(:,k)';    bigNprimeqp = bigNprime(:,:,k);                                      xkfac(k)=wtq(k)*detj(k);    me = me + xkfac(k)*(bigNqp' * bigNqp + dampfac * bigNprimeqp' *  bigNprimeqp);      end